import React, {Component} from 'react';
import Question from "../question/Question";
import Answer from "../answer/Answer";
import './SurveyContent.css'
export default class Quiz extends Component {

    // initiating the local state
    state = {
        quiestions: {
            1: 'How old are you?',
            2: 'Select your gender',
            3: 'Do you have a smoking habit?',
            4: "Are you a health worker? (Doctors, nurses, support staff, etc.)",
            5: "I have Shortness of breath",
            6: "I have Dry cough",
            7: "Do you agree I have Sore throat?",
            8: "I feal Extreme weakness (e.g. needing help to get to the bathroom)",
            9: "I have Cold or runny nose",
            10: "Please let us know what your current body temperature is?",
            11: '',


        },
        answers: {
            1: {
                1: 'Chicago',
                2: 'New Orleans',
                3: 'New York'
            },
            2: {
                1: 'Athens',
                2: 'Patras',
                3: 'Kalamata'
            },
            3: {
                1: 'Krypton',
                2: 'Mars',
                3: 'Saturn'
            },
            4: {
                1: 'under 18',
                2: '18-40',
                3: '45+'
            }
        },
        correctAnswers: {
            1: '0',
            2: '1',
            3: '1',
            4:"0"
        },
        correctAnswer: 0,
        clickedAnswer: 0,
        step: 1,
        score: 0
    }

    // the method that checks the correct answer
    checkAnswer = answer => {
        const { correctAnswers, step, score } = this.state;
        if(answer === correctAnswers[step]){
            this.setState({
                score: score + 1,
                correctAnswer: correctAnswers[step],
                clickedAnswer: answer
            });
        }else{
            this.setState({
                correctAnswer: 0,
                clickedAnswer: answer
            });
        }
    }

    // method to move to the next question
    nextStep = (step) => {
        this.setState({
            step: step + 1,
            correctAnswer: 0,
            clickedAnswer: 0
        });
    }

    render(){
        let { quiestions, answers, correctAnswer, clickedAnswer, step, score } = this.state;
        return(
            <div className="Content">
                {step <= Object.keys(quiestions).length ?
                    (<>
                        <Question
                            question={quiestions[step]}
                        />
                        <Answer
                            answer={answers[step]}
                            step={step}
                            checkAnswer={this.checkAnswer}
                            correctAnswer={correctAnswer}
                            clickedAnswer={clickedAnswer}
                        />
                        <button
                            className="NextStep"
                            disabled={
                                clickedAnswer && Object.keys(quiestions).length >= step
                                    ? false : true
                            }
                            onClick={() => this.nextStep(step)}>Next</button>
                    </>) : (
                        <div className="finalPage">
                            <h1>You have completed the quiz!</h1>
                            <p>Your score is: {score} of {Object.keys(quiestions).length}</p>
                            <p>Thank you!</p>
                        </div>
                    )
                }
            </div>
        );
    }
}
